global $alink;
// dump vars of alink into the postrow block
$postrow = array_merge($postrow, $alink->get_linked_account_settings($poster_id));
// END Account Link

// START Account Link
// dump vars of alink into the postrow block
$alink->get_linked_account_postrow($poster_id);
